var getMultiselectList = function ($multiselectTrigger) {
    var testid = $multiselectTrigger.attr('data-testid');
    return cy.get("[data-testid='" + testid + "--multiSelect'").find('[data-testid="_multiselect-menu-list"]');
};
/**
 * A cypress helper to get a option based on it's index.
 * This helper yields the found option. It can be chanined with the `toggleOption` method.
 *
 * @example
 * ```ts
 *    cy.get('[data-testid="myTestId]')
 *      .then(getOptionByIndex(2))
 * ```
 */
export var getOptionByIndex = function (index) { return function ($multiselect) {
    return cy
        .wrap($multiselect)
        .then(getMultiselectList)
        .then(function ($list) {
        return cy.wrap($list.children()[index]);
    });
}; };
/**
 * A cypress helper to get a option based on it's label
 * This helper yields the found option. It can be chanined with the `toggleOption` method.
 *
 * @example
 * ```ts
 *    cy.get('[data-testid="myTestId]')
 *      .then(getOption('Option 1'))
 * ```
 */
export var getOption = function (option) { return function ($multiselect) {
    return cy
        .wrap($multiselect)
        .then(getMultiselectList)
        .contains('li', option);
}; };
/**
 * A cypress helper to toggle an option as checked or unchecked, based on it's current state
 * This helper yields the toggled option.
 *
 * @example
 * ```ts
 *    cy.get('[data-testid="myTestId]')
 *      .then(toggleOption('Option 1'))
 * ```
 */
export var toggleOption = function (option) { return function ($multiselect) {
    var optionSelector = typeof option === 'string' ? getOption : getOptionByIndex;
    optionSelector(option)($multiselect)
        .as('toggledOption')
        .click();
    return cy.get('@toggledOption');
}; };
/**
 * A cypress helper to toggle a multiselect open or closed.
 * This helper yields the multiselect and can be chained with any other method.
 *
 * @example
 * ```ts
 *    cy.get('[data-testid="myTestId]')
 *      .then(toggleMultiselect)
 *      .then(toggleOption('Option 2'))
 * ```
 */
export var toggleMultiselect = function ($multiselect) {
    cy.wrap($multiselect).click();
    return cy.wrap($multiselect);
};
