// sort by column name
export var sortByColumn = function (columnName) { return function ($element) {
    cy.wrap($element)
        .find("[data-testid='_table-header-cell']")
        .contains(columnName)
        .click();
    return cy.wrap($element);
}; };
// click a row based on its content
export var clickRowByContent = function (content) { return function ($element) {
    cy.wrap($element)
        .find("[data-testid='_table-row']")
        .contains(content)
        .click();
    return cy.wrap($element);
}; };
// find a row by index or content
export var findRowByIndex = function (index) { return function ($element) {
    return cy
        .wrap($element)
        .find("[data-testid='_table-row']")
        .eq(index);
}; };
// find a row by content
export var findRowByContent = function (content) { return function ($element) {
    return cy
        .wrap($element)
        .find("[data-testid='_table-row']")
        .contains(content);
}; };
// open action row
export var openActionRow = function (content) { return function ($element) {
    cy.wrap($element)
        .find("[data-testid='_table-cell-action']")
        .click();
    return cy.wrap($element).find("[data-testid='_table-cell-action']");
}; };
