/* eslint-disable no-undef */
function highlightHomeOnLoad() {
    if (!window.location.hash) {
        styleGuideHeader.classList.add('active');
    }
}
function setHomeLink() {
    var logo = document.querySelector('div[class*="logo"] > h1');
    var homeLink = document.createElement('a');
    homeLink.setAttribute('href', '/');
    homeLink.setAttribute('style', 'text-decoration: none');
    logo.parentNode.insertBefore(homeLink, logo);
    homeLink.appendChild(logo);
}
function changeSearchPlaceholder() {
    document.querySelector('div[class*="sidebar"] input').placeholder = 'Search';
}
function highlightNav(target) {
    var urlEncoded = /%20/g;
    var space = /\s/g;
    document.querySelectorAll('a').forEach(function (a) {
        a.classList.remove('active');
    });
    setTimeout(function () {
        var params = window.location.href.split('/').pop();
        if (params.toLowerCase() === target.innerText.replace(space, '').toLowerCase() ||
            params.replace(urlEncoded, ' ') === target.innerText.replace(space, ' ')) {
            target.classList.add('active');
        }
    }, 0);
}
function addNavHighlighterListener() {
    var sideBar = document.querySelector('nav');
    sideBar.addEventListener('click', function (e) {
        highlightNav(e.target);
    });
}
function reformatLinks() {
    var links = document.querySelectorAll('nav ul ul a');
    links.forEach(function (link) {
        link.innerText = link.innerText.replace(/([A-Z])/g, ' $1');
    });
}
document.addEventListener('DOMContentLoaded', function () {
    highlightHomeOnLoad();
    setHomeLink();
    changeSearchPlaceholder();
    addNavHighlighterListener();
    reformatLinks();
});
